import com.github.barteksc.pdfviewer.util.FitPolicy;
import com.github.barteksc.pdfviewer.PDFView;

public class FilteredFilePOJOViewModel extends AndroidViewModel {
    // ... existing code ...

    public void initializePdfRenderer(FileObjectType fileObjectType, String file_path, Uri data, boolean fromThirdPartyApp) {
        // ... existing code ...

        try {
            File pdfFile = new File(file_path);
            isPdfPasswordProtected = isPdfEncrypted(pdfFile);
            if (!isPdfPasswordProtected) {
                total_pages = getPageCount(pdfFile);
            }
        } catch (IOException e) {
            // Handle exception
        }

        asyncTaskStatus.postValue(AsyncTaskStatus.COMPLETED);
