    private boolean isPdfEncrypted(File pdfFile) throws IOException {
        ParcelFileDescriptor fd = ParcelFileDescriptor.open(pdfFile, ParcelFileDescriptor.MODE_READ_ONLY);
        PdfRenderer renderer = new PdfRenderer(fd);
        boolean isEncrypted = renderer.getPageCount() == 0; // If page count is 0, it's likely encrypted
        renderer.close();
        fd.close();
        return isEncrypted;
    }

    private int getPageCount(File pdfFile) throws IOException {
        ParcelFileDescriptor fd = ParcelFileDescriptor.open(pdfFile, ParcelFileDescriptor.MODE_READ_ONLY);
        PdfRenderer renderer = new PdfRenderer(fd);
        int pageCount = renderer.getPageCount();
        renderer.close();
        fd.close();
        return pageCount;
    }

    // ... rest of the existing code ...
}
